    public function unsetParam($key){
        $p=$this->getFlag(IGK_PARAMS_FLAG);
        unset($p[$key]);
        if(igk_count($p)<=0)
            $this->unsetFlag(IGK_PARAMS_FLAG);
        else{
            $this->setFlag(IGK_PARAMS_FLAG, $p);
        }
    }
