        $c=$this->getChilds();
        if($c){
            if($name == "*"){
                $tab=array_merge($tab, $c->ToArray());
                foreach($c as $k){
                    $c=$k->getElementsByTagName($s);
                    if(is_array($c) && (count($c) > 0))
