    ///<summary>Remove childs</summary>
    public function remove($child, $changeParent=1){
        $v_deleted=false;
        $c=$this->getChilds();
        if(($c == null) || (!isset($child)))
            return $v_deleted;
        for($i=0; $i < igk_count($c); $i++){
            if(isset($c[$i])){
                if(($c[$i] === $child)){
                    unset($c[$i]);
                    if($changeParent)
                        $child->setParentNode(null, __FUNCTION__);
                    $v_deleted=true;
                    $i--;
                    continue;
                }
            }
            else{
                igk_debug_wln(" index ".$i." not found ". $child["id"]);
            }
        }
        return $v_deleted;
    }
