    static public function CreateElement($name, $param=null){
        if(!empty($name)){
            if(IGKString::StartWith($name, 'igk:')){
                $f=igk_createnode(substr($name, 4), null, $param);
                if($f)
                    return $f;
            }
            $tb=explode(':', $name);
            if(count($tb) == 1){
