///<summary>Represente class: IGKHtmlSessionBlockNode</summary>
final class IGKHtmlSessionBlockNode extends IGKHtmlCtrlNodeItemBase {
    private $m_owner;
    ///<summary>Represente __AcceptRender function</summary>
    ///<param name="o" default="null"></param>
    protected function __AcceptRender($o=null){
        if(!$o || !$o->Document){
            return false;
        }
        $key=".@".get_class($this)."/rendered";
        if(isset($o->{$key}) && $o->{$key}){
            return false;
        }
        $o->{$key}
        =1;
        if(igk_xml_is_mailoptions($o) || igk_xml_is_cachingrequired($o))
            return false;
        $this->ClearChilds();
        $v=$this->__buildview();
        $t=$this;
        $t->addNodeCallback("mem_usage", function($t){
            return $t->addMemoryUsageInfoNode();
        });
        $t->addJSReadyScript('igk.ctrl.sessionblock.init');
        return $this->getIsVisible();
    }
    ///<summary>Represente __buildview function</summary>
    private function __buildview(){
        $cnf_=igk_getctrl(IGK_CONF_CTRL);
        $t=$this;
        $t["class"]="debugzone igk-session-block google-Roboto";
        $t->setIndex(10000);
        $d=$t->add("div");
        $d["class"]="google-Roboto";
        $d->addSectionTitle(4)->
        Content=__("Debug Panel");
        $ul=$d->add("ul");
        $ul=$d->add("ul");
        if(!igk_get_env("sys://error")){
            $ul["class"]="btn-group action-group";
            $ul["style"]="max-width: 300px";
            $ul->addLi()->setClass("btn btn-default igk-btn igk-btn-default")->add("a", array("href"=>$t->m_owner->getUri("forceview")))->
            Content="ForceView";
            $a=$ul->addLi()->setClass("btn btn-default igk-btn igk-btn-default")->addAClearSAndReload()->clearClass()->setContent("ClearSession");
        }
        if(igk_is_conf_connected())
            $ul->addLi()->setClass("btn btn-default igk-btn igk-btn-default")->add("a", array("href"=>$cnf_->getUri('logout')))->
        Content=R::gets("Logout");
        if(igk_app()->CurrentPageFolder != "Configs"){
            $ul->addLi()->setClass("btn btn-default igk-btn igk-btn-default")->add("a", array("href"=>new IGKHtmlRelativeUriValueAttribute("/Configs")))->
            Content=R::gets("Configure");
        }
        $ul->addLi()->setClass("btn btn-default igk-btn igk-btn-default")->addAJXA($cnf_->getUri('preview_result_ajx'))->
        Content="PreviewResult";
        $ul->addLi()->setClass("btn btn-default igk-btn igk-btn-default")->add("a", array("href"=>new IGKHtmlRelativeUriValueAttribute(IGK_BASE_DIR."/")))->
        Content="Index";
        if(IGKServerInfo::IsLocal() || igk_is_conf_connected() || !igk_sys_env_production()){
            $ul->addLi()->setClass("btn btn-default igk-btn igk-btn-default")->add("a", array("href"=>igk_getctrl(IGK_SESSION_CTRL)->getUri("clearcache")))->
            Content=__("Clear Cache");
        }
        $ul->addVisible("igk_is_conf_connected")->addLi()->setClass("btn btn-default igk-btn igk-btn-default")->addA(igk_io_baseUri()."/resetDb")->
        Content=__("Reset DB");
        $ul=$d->add("ul")->setId("cnf-inf");
        $ul->addLi()->
        Content="Referer : ". igk_server()->REMOTE_ADDR;
        $ul->addLi()->
        Content="PHP VERSION : ". PHP_VERSION;
        $ul->addLi()->
        Content="CurrentLang : ". R::GetCurrentLang();
        $ul->addLi()->
        Content="CurrentPage : ". igk_app()->CurrentPage;
        $ul->addLi()->
        Content="CurrentFolder : ". igk_app()->CurrentPageFolder;
        $ul->addLi()->
        Content="ViewMode : ". IGKViewMode::GetSystemViewMode();
        $ul->addLi()->
        Content="Environment : ". array("development", "production")[igk_sys_env_production()];
        $ul->addLi()->
        Content=new IGKSessionIdValue();
        $bootstrap=igk_sys_getconfig("BootStrap.Enabled");
        $tab=igk_sys_debug_components();
        if($tab){
            foreach($tab as $k=>$v){
                $v($this, $this->m_owner);
            }
        }
        if(!igk_is_conf_connected() && ($this->CurrentPageFolder != IGK_CONFIG_PAGEFOLDER)){
            $logindiv=$t->add("div");
            $logindiv->setCallback("getIsVisible", igk_create_expression_callback("return igk_app()->CurrentPageFolder !== IGK_CONFIG_PAGEFOLDER;", null));
            $frm=$logindiv->addForm();
            $frm["action"]=igk_getconfigwebpagectrl()->getUri("connectToConfig");
            $frm["class"]="igk-debug-connect-form";
            igk_html_form_initfield($frm);
            $v_logul=$frm->addPanelBox()->add("ul");
            $li=$v_logul->addLi();
            $li->add("label")->setAttribute("for", "clAdmLogin")->
            Content=__("Login");
            $input=$li->addInput("clAdmLogin");
            $input["autocomplete"]="off";
            $input["placeholder"]=R::ngets("tip.login");
            $input->setClass("igk-form-control");
            $li=$v_logul->addLi();
            $li->addLabel()->
            Content=__("Password");
            $i=$li->addInput("clAdmPwd", "password");
            $i["placeholder"]=R::ngets("tip.password")->getValue();
            $i["autocomplete"]="current-password";
            $i->setClass("igk-form-control");
            $i=$v_logul->addLi()->addInput("btn.connect", "submit", R::ngets("btn.connect"));
            if($bootstrap){
                $i->setClass("btn btn-default igk-btn igk-btn-default");
            }
        }
        else{
            $div=$t->addDiv()->setClass("igk-form-group");
            if(igk_is_conf_connected()){
                $sl=$div->addSelect("clViewMode")->setClass("igk-form-control");
                $uri=$t->m_owner->getUri("changeviewmode");
                $sl["onchange"]="javascript: ns_igk.ajx.get('{$uri}&mode='+this.value,null, ns_igk.ajx.fn.replace_body); return false;";
                $mode=igk_app()->getViewMode();
                foreach(igk_get_class_constants("IGKViewMode") as $k=>$v){
                    $opt=$sl->add("option")->setContent($k)->setAttribute("value", $v);
                    if($v == $mode){
                        $opt["selected"]=true;
                    }
                }
            }
            $t->addDiv()->setClass("igk-cleartab");
        }
    }
    ///<summary>Represente __construct function</summary>
    ///<param name="tag"></param>
    ///<param name="ctrl"></param>
    public function __construct($tag, $ctrl){
        parent::__construct($tag);
        $this->
        m_owner=$ctrl;
    }
    ///<summary>Represente onAppExit function</summary>
    public function onAppExit(){
        $app=igk_app();
        if(igk_is_ajx_demand() && $this->IsVisible && $app->Session->getRedirectTask('modview')){
            $this->RenderAJX();
            $app->Session->{"modeview"}
            =null;
        }
    }
}
