///<summary></summary>
final class IGKHtmlMailDoc extends IGKHtmlItem {
    private $_attachement;
    private $m_app;
    private $m_message;
    private $m_theme;
    ///<summary>Represente __AcceptRender function</summary>
    ///<param name="opt" default="null"></param>
    public function __AcceptRender($opt=null){
        return true;
    }
    ///<summary>Represente __construct function</summary>
    public function __construct(){
        $this->
        m_app=igk_app();
        if($this->m_app == null)
            igk_die("apps not initialize");
        parent::__construct("div");
        $this["class"]="igk-mail";
        $this->
        m_theme=new IGKHtmlDocTheme($this->m_app, __CLASS__.":theme");
        $this->_initTheme();
        $this->
        m_message=$this->addDiv();
        $this->setId("message");
    }
    ///<summary>Represente _copyAddBuildDefinition function</summary>
    ///<param name="target"></param>
    ///<param name="source"></param>
    private function _copyAddBuildDefinition($target, $source){
        $selector=array();
        foreach($source->def->Attributes as $k=>$v){
            $tab=explode(',', $k);
            if(empty($v))
                continue;
            foreach($tab as $s=>$t){
                if(!empty($t)){
                    $selector[trim($t)
                    ]=$v;
                }
            }
        }
        foreach($selector as $k=>$v){
            if(IGKString::StartWith($k, ".igk-mail")){
                $target->def[$k.
                ",.a3s > div "]=$v;
            }
            else{
                $target->def[".igk-mail ".$k.",.a3s > div ".
                $k]=$v;
            }
        }
    }
    ///<summary>Represente _initTheme function</summary>
    private function _initTheme(){}
    ///<summary>Represente CreateFromDocument function</summary>
    ///<param name="doc"></param>
    static public function CreateFromDocument($doc){
        if($doc == null)
            return null;
        $c=new IGKHtmlMailDoc();
        $c->
        m_doc=$doc;
        return $c;
    }
    ///<summary>Represente getAttachement function</summary>
    public function getAttachement(){
        return $this->_attachement;
    }
    ///<summary>get the message node in this mail definition</summary>
    public function getMessage(){
        return $this->m_message;
    }
    ///<summary>get the theme used in this mail definition</summary>
    public function getTheme(){
        return $this->m_theme;
    }
    ///<summary>Represente innerHTML function</summary>
    ///<param name="options" default="null" ref="true"></param>
    protected function innerHTML(& $options=null){
        $out="";
        $s=new IGKHtmlItem("style");
        $s["type"]="text/css";
        $s->
        Content=$this->m_theme->get_css_def(true);
        if($this->m_doc != null){
            $out .= "<head>".$this->m_doc->head->innerHTML($options);
            $out .= $s->Render($options)."</head>";
            $out .= $this->m_doc->body->Render($options);
        }
        else{
            $out .= "<head>".$s->Render($options)."</head>";
            $out .= parent::innerHTML($options);
        }
        unset($s);
        return $out;
    }
    ///<summary>Represente loadTheme function</summary>
    ///<param name="theme"></param>
    public function loadTheme($theme){
        $this->_copyAddBuildDefinition($this->m_theme, $theme);
        foreach($theme->getMedias() as $k=>$m){
            $r=$this->m_theme->reg_media($k);
            if($r)
                $this->_copyAddBuildDefinition($r, $m);
        }
    }
    ///<summary>Represente Render function</summary>
    ///<param name="o" default="null"></param>
    public function Render($o=null){
        return $this->renderDoc();
    }
    ///<summary>Represente renderDoc function</summary>
    public function renderDoc(){
        $this->
        _attachement=new IGKMailAttachementContainer();
        $p=igk_xml_create_render_option();
        $p->
        Context="mail";
        $p->
        Attachement=$this->_attachement;
        $s="<!DOCTYPE ".IGK_DOC_TYPE." >";
        $s .= "<html ";
        $s .= trim($this->getAttributeString($p));
        $s .= ">";
        $s .= $this->innerHTML($p);
        $s .= "</html>";
        return $s;
    }
    ///<summary>Represente sendMail function</summary>
    ///<param name="to"></param>
    ///<param name="from"></param>
    ///<param name="subject"></param>
    public function sendMail($to, $from, $subject){
        $src=$this->Render();
        $g=igk_mail_sendmail($to, $from, $subject, $src, null, $this->_attachement ? $this->_attachement->getList(): null, "text/html");
        return $g;
    }
}
