///<summary>represent an object used to manage document's meta data.</summary>
final class IGKHtmlMetaManager extends IGKObject implements Serializable {
    const ATTR_CONTENT="content";
    const META_AUTHOR=0x1;
    const META_CONTENT_TYPE=self::META_AUTHOR + 0x4;
    const META_COPYRIGHT=self::META_AUTHOR + 0x1;
    const META_DESC=self::META_AUTHOR + 0x2;
    const META_GENERATOR=self::META_AUTHOR + 0x5;
    const META_KEYWORDS=self::META_AUTHOR + 0x3;
    private $m_metas;
    ///<summary>ownerDoc is used to initialize data</summary>
    public function __construct($doc){
        $this->_initMetas();
    }
    ///<summary>Represente __toString function</summary>
    public function __toString(){
        return __CLASS__;
    }
    ///<summary>Represente _initMetas function</summary>
    private function _initMetas(){
        $pmetas=null;
        $this->
        m_metas=array();
        $this->m_metas[self::
        META_AUTHOR]=array("name"=>"author", self::ATTR_CONTENT=>IGK_AUTHOR);
        $this->m_metas[self::
        META_COPYRIGHT]=array("name"=>"copyright", self::ATTR_CONTENT=>IGK_COPYRIGHT);
        $this->m_metas[self::
        META_DESC]=array(
            "name"=>"Description",
            self::ATTR_CONTENT=>$pmetas ? $pmetas->Description: IGK_STR_EMPTY
        );
        $this->m_metas[self::
        META_KEYWORDS]=array(
            "name"=>"keywords",
            self::ATTR_CONTENT=>$pmetas ? $pmetas->Keywords: IGK_STR_EMPTY
        );
        $this->m_metas[self::
        META_CONTENT_TYPE]=array(
            "http-equiv"=>"Content-Type",
            self::ATTR_CONTENT=>"text/html; charset=utf-8"
        );
        $this->m_metas[self::
        META_GENERATOR]=array(
            "name"=>"generator",
            self::ATTR_CONTENT=>igk_app_version()
        );
        igk_reg_hook("html_meta", function(){});
    }
    ///<summary>Represente addMeta function</summary>
    ///<param name="name"></param>
    ///<param name="meta"></param>
    public function addMeta($name, $meta){
        $bmeta=igk_getv($this->m_metas, $name);
        if($bmeta && ($bmeta !== $meta)){
            unset($this->m_metas[$name]);
        }
        if($meta && !isset($this->m_metas[$name])){
            $this->
            m_metas[$name]=$meta->attributes->toArray();
            $this->
            m_metas[$name]["changed"]=1;
            return 1;
        }
        return 0;
    }
    ///<summary>Represente getAuthor function</summary>
    public function getAuthor(){
        return IGKHtmlUtils::GetValue($this->m_metas[self::META_AUTHOR][self::ATTR_CONTENT]);
    }
    ///<summary>Represente getContentType function</summary>
    public function getContentType(){
        return IGKHtmlUtils::GetValue($this->m_metas[self::META_CONTENT_TYPE][self::ATTR_CONTENT]);
    }
    ///<summary>Represente getCopyright function</summary>
    public function getCopyright(){
        return IGKHtmlUtils::GetValue($this->m_metas[self::META_COPYRIGHT][self::ATTR_CONTENT]);
    }
    ///<summary>Represente getDescription function</summary>
    public function getDescription(){
        return IGKHtmlUtils::GetValue($this->m_metas[self::META_DESC][self::ATTR_CONTENT]);
    }
    ///<summary>Represente getKeywords function</summary>
    public function getKeywords(){
        return IGKHtmlUtils::GetValue($this->m_metas[self::META_KEYWORDS][self::ATTR_CONTENT]);
    }
    ///<summary>Represente getMetaById function</summary>
    ///<param name="name"></param>
    public function getMetaById($name){
        if(isset($this->m_metas[$name])){
            return $this->m_metas[$name];
        }
        return null;
    }
    ///<summary>Represente Render function</summary>
    ///<param name="options" default="null"></param>
    public function Render($options=null){
        $handle=0;
        $s=igk_ob_get_func(function() use (& $handle){
            $handle=igk_hook(IGKEvents::HOOK_HTML_META);
        });
        if($handle){
            return $s;
        }
        $LF="";
        $o="";
        foreach($this->m_metas as $k=>$v){
            $o .= "<meta ";
            foreach($v as $k=>$v){
                if($k == "changed")
                    continue;
                $o .= $k."=\"".IGKHtmlUtils::GetAttributeValue($v)."\" ";
            }
            $o .= "/>".$LF;
        }
        return $o.$s;
    }
    ///<summary>Represente rmMeta function</summary>
    ///<param name="name"></param>
    public function rmMeta($name){
        if(isset($this->m_metas[$name])){
            unset($this->m_metas[$name]);
        }
    }
    ///<summary>Represente serialize function</summary>
    public function serialize(){
        $g=array();
        foreach($this->m_metas as $k=>$v){
            if(isset($v['changed']) && $v['changed']){
                $t=array_slice($v, 0);
                unset($t["changed"]);
                $g[$k]=$t;
            }
        }
        if(count($g) > 0)
            return json_encode($g);
        return '';
    }
    ///<summary>Represente setAttribute function</summary>
    ///<param name="key"></param>
    ///<param name="attr"></param>
    ///<param name="value"></param>
    public function setAttribute($key, $attr, $value){
        $this->
        m_metas[$key][$attr]=$value;
        $this->
        m_metas[$key]["changed"]=1;
    }
    ///<summary>Represente setAuthor function</summary>
    ///<param name="value"></param>
    public function setAuthor($value){
        $this->updateContent(self::META_AUTHOR, $value);
    }
    ///<summary>Represente setContentType function</summary>
    ///<param name="value"></param>
    public function setContentType($value){
        $this->updateContent(self::META_CONTENT_TYPE, $value);
    }
    ///<summary>Represente setCopyright function</summary>
    ///<param name="value"></param>
    public function setCopyright($value){
        $this->updateContent(self::META_COPYRIGHT, $value);
    }
    ///<summary>Represente setDescription function</summary>
    ///<param name="value"></param>
    public function setDescription($value){
        $this->updateContent(self::META_DESC, $value);
    }
    ///<summary>Represente setKeywords function</summary>
    ///<param name="value"></param>
    public function setKeywords($value){
        $this->updateContent(self::META_KEYWORDS, $value);
    }
    ///<summary>Represente unserialize function</summary>
    ///<param name="v"></param>
    public function unserialize($v){
        $this->_initMetas();
        if(!empty($v) && ($tab=json_decode($v))){
            foreach($tab as $k=>$v){
                unset($v->changed);
                $this->
                m_metas[$k]=(array)$v;
            }
        }
    }
    ///<summary>Represente updateContent function</summary>
    ///<param name="key"></param>
    ///<param name="value"></param>
    public function updateContent($key, $value){
        if(!isset($this->m_metas[$key][self::ATTR_CONTENT]) || ($this->m_metas[$key][self::ATTR_CONTENT] != $value)){
            $this->m_metas[$key][self::
            ATTR_CONTENT]=$value;
            $this->
            m_metas[$key]["changed"]=1;
        }
    }
}
///<summary> represent a html/xml reader info</summary>
final class IGKHtmlReader extends IGKObject {
    private $m_attribs;
    private $m_hasAttrib;
    private $m_hfile;
    private $m_isEmpty;
    private $m_mmodel;
    private $m_name;
    private $m_nodes;
    private $m_nodetype;
    private $m_offset;
    private $m_resolvKeys;
    private $m_resolvValues;
    private $m_text;
    private $m_v;
    public $context;
    static $ss;
    static private $sm_ItemCreatorListener;
    static private $sm_openertype;
    ///<summary>Represente __construct function</summary>
    ///<param name="text"></param>
    private function __construct($text){
        $this->
        m_text=$text;
        $this->
        m_offset=0;
        $this->
        m_nodetype=IGKXMLNodeType::NONE;
        $this->
        m_resolvKeys=array();
        $this->
        m_resolvValues=array();
        $this->
        m_attribs=null;
        $this->
        m_nodes=array();
    }
    ///<summary>Represente __readBracket function</summary>
    ///<param name="text"></param>
    ///<param name="offset" ref="true"></param>
    ///<param name="start" default="'['"></param>
    ///<param name="end" default="']'"></param>
    static private function __readBracket($text, & $offset, $start='[', $end=']'){
        $c=0;
        $m="";
        $ct=strlen($text);
        while(($offset < $ct)){
            $ch=$text[$offset];
            $m .= $ch;
            $offset++;
            if($ch == $end){
                if($c == 0){
                    break;
                }
                $c--;
            }
            else if($ch == $start){
                $c++;
            }
        }
        return $m;
    }
    ///<summary>Represente __readTextValue function</summary>
    ///<param name="v" default="IGK_STR_EMPTY"></param>
    private function __readTextValue($v=IGK_STR_EMPTY){
        while($this->CanRead()){
            $v .= $this->m_text[$this->m_offset];
            $this->m_offset++;
            if(substr($v, -1, 1) == "<"){
                $v=substr($v, 0, strlen($v)-1);
                $this->m_offset--;
                $this->
                m_name=null;
                $this->
                m_v=$v;
                $this->
                m_nodetype=IGKXMLNodeType::TEXT;
                return true;
            }
        }
        return false;
    }
    ///<summary>Represente _addNode function</summary>
    ///<param name="cnode"></param>
    ///<param name="n"></param>
    ///<param name="tab"></param>
    ///<param name="args"></param>
    private function _addNode($cnode, $n, $tab, $args){
        $g=explode(':', $n);
        if(igk_count($g) == 1){
            $v=self::CreateNode($n, $args);
            if(!$cnode->add($v) && !self::_AddToParent($tab, $cnode, $v)){
                $b=$tab->add($v);
            }
            else{
                $b=$v;
            }
            return $b;
        }
        $k=call_user_func_array(array($cnode, IGK_ADD_PREFIX.$g[1]), $args != null ? $args: array());
        $this->_appendResolvNode($n, $k, $cnode);
        return $k;
    }
    ///<summary>Represente _AddToParent function</summary>
    ///<param name="topnode"></param>
    ///<param name="cnode"></param>
    ///<param name="node"></param>
    static private function _AddToParent($topnode, $cnode, $node){
        $p=$cnode->ParentNode;
        while($p && ($p !== $topnode)){
            if($p->add($node) !== null){
                return 1;
            }
            $p=$p->ParentNode;
        }
        return 0;
    }
    ///<summary>Represente _appendResolvNode function</summary>
    ///<param name="n"></param>
    ///<param name="k"></param>
    ///<param name="cnode"></param>
    private function _appendResolvNode($n, $k, $cnode){
        if($k){
            $this->
            m_resolvKeys[]=$n;
            $this->
            m_resolvValues[]=$k;
            $this->
            m_nodes[]=$cnode;
        }
    }
    ///<summary>Represente _BuildNode function</summary>
    ///<param name="reader"></param>
    ///<param name="cnode"></param>
    ///<param name="name"></param>
    ///<param name="tab_doc"></param>
    ///<param name="pargs"></param>
    static private function _BuildNode($reader, $cnode, $name, $tab_doc, $pargs){
        if((self::$sm_openertype == "XML") || (isset($reader->context) && ($reader->context == IGKHtmlContext::XML))){
            $n=self::CreateNode($name, $pargs);
            if($cnode){
                $cnode->add($n);
            }
            return $n;
        }
        else{
            return ($cnode !== null) ? $reader->_addNode($cnode, $name, $tab_doc, $pargs): self::CreateNode($name, $pargs);
        }
    }
    ///<summary>Represente _LoadComplete function</summary>
    ///<param name="cnode"></param>
    ///<param name="tag"></param>
    private function _LoadComplete($cnode, $tag){
        $m=(strtolower($cnode->tagName) == strtolower($tag));
        if($m){
            $cnode->loadingComplete();
            return $cnode->getParentNode();
        }
        $n=count($this->m_resolvKeys) > 0 ? $this->m_resolvKeys[count($this->m_resolvKeys)-1]: null;
        $d=count($this->m_resolvValues) > 0 ? $this->m_resolvValues[count($this->m_resolvValues)-1]: null;
        if((strtolower($n) == strtolower($tag)) && ($d === $cnode)){
            array_pop($this->m_resolvKeys);
            array_pop($this->m_resolvValues);
        }
        $cnode->loadingComplete();
        $b=array_pop($this->m_nodes);
        $pnode=$cnode->getParentNode();
        if(($b !== null) && ($pnode !== $b)){
            $pnode=$b;
        }
        $cnode=$pnode;
        return $cnode;
    }
    ///<summary>read the model</summary>
    ///<param name="context">name of the function that call the read model</param>
    static private function _ReadModel($reader, $tab_doc, $text, $context=null){
        $cnode=null;
        $pnode=null;
        $vp_item=(object)array("clCurrent"=>null, "clParent"=>null);
        if((self::$sm_openertype == null) && ($reader->context != null)){
            self::
            $sm_openertype=$reader->context;
        }
        $v_tags=array();
        $_shift_setting=function($n, $cnode, & $v_tags, & $krsv){
            if(igk_count($v_tags)<=0)
                return;
            $s=array_shift($v_tags);
            if($s->clName == $n){
                if($cnode === $s->item){
                    $krsv=false;
                }
                else
                    array_unshift($v_tags, $s);
            }
        };
        while($reader->Read()){
            switch($reader->NodeType){
                case IGKXMLNodeType::ELEMENT:
                $name=$reader->Name();
                if(empty($name)){
                    break;
                }
                $cattr=$reader->Attribs();
                if($context == "LoadExpression"){
                    $v_tn=new IGKHtmlItem($name);
                    $v_tn->startLoading(__CLASS__, $context);
                    if($reader->HasAttrib()){
                        foreach($cattr as $k=>$c){
                            $v_tn->offsetSetExpression($k, $c);
                        }
                    }
                    if($cnode == null){
                        $tab_doc->add($v_tn);
                    }
                    else{
                        $cnode->add($v_tn);
                        if($v_tn->ParentNode !== $cnode){
                            $ht=$cnode->ParentNode;
                            while($ht){
                                if($ht->add($v_t)->ParentNode === $ht)
                                    break;
                                $ht=$ht->ParentNode;
                            }
                            if($ht === null){
                                $tab_doc->add($v_tn);
                            }
                            else
                                igk_die("failed ".($ht === null));
                        }
                    }
                    $cnode=$v_tn;
                    if($reader->IsEmpty()){
                        $cnode=$reader->_LoadComplete($cnode, $name);
                        if($cnode == $tab_doc){
                            $cnode=null;
                            $reader->
                            m_nodes=array();
                        }
                    }
                }
                else{
                    $pargs=igk_engine_get_attr_arg(igk_getv($cattr, "igk:args"), $reader->context);
                    $v_tn=self::_BuildNode($reader, $cnode, $name, $tab_doc, $pargs);
                    if($v_tn){
                        if($v_tn->tagName && ($v_tn->tagName != $name) && !$reader->IsEmpty()){
                            array_unshift($v_tags, (object)array("clName"=>$name, "item"=>$v_tn));
                            if($cnode == null)
                                $reader->_appendResolvNode($name, $v_tn, $cnode);
                        }
                        $v_tn->startLoading(__CLASS__, $context);
                        if($reader->HasAttrib()){
                            foreach($cattr as $k=>$c){
                                if($k == "igk:args")
                                    continue;
                                if(self::$sm_openertype == "XML"){
                                    $v_tn->setAttribute($k, $c);
                                }
                                else
                                    $v_tn[$k]=$c;
                            }
                        }
                        if($cnode == null){
                            $tab_doc->add($v_tn);
                        }
                        $cnode=$v_tn;
                        if($reader->IsEmpty()){
                            $cnode=$reader->_LoadComplete($cnode, $name);
                            if($cnode == $tab_doc){
                                $cnode=null;
                                $reader->
                                m_nodes=array();
                            }
                        }
                    }
                    else{
                        igk_ilog(__FILE__.":".__LINE__.": Node [".$name."]  not created", __METHOD__);
                        $reader->Skip();
                    }
                }
                break;
                case IGKXMLNodeType::TEXT:
                $v_sr=$reader->getValue()."";
                $v_c=IGK_STR_EMPTY;
                if(strlen(trim($v_sr)) > 0){
                    if($cnode){
                        if($cnode->isEmptyTag()){
                            $txt=new IGKHtmlText();
                            $txt->setContent($v_sr);
                            $cnode->parentNode->add($txt);
                            $cnode=$cnode->parentNode;
                        }
                        else{
                            if($cnode->getTempFlag("replaceContentLoading") || (($cnode->Content == "") && !$cnode->HasChilds))
                                $cnode->
                            Content=$v_sr;
                            else{
                                $txt=new IGKHtmlText();
                                $txt->setContent($v_sr);
                                $cnode->add($txt);
                            }
                        }
                    }
                    else{
                        $v=igk_html_node_text($v_sr);
                        $tab_doc->add($v);
                    }
                }
                break;
                case IGKXMLNodeType::COMMENT:
                $v_v=$reader->getValue();
                $v=new IGKHTMLCommentItem($v_v);
                if(!$cnode){
                    $tab_doc->add($v);
                }
                else{
                    $cnode->add($v);
                }
                break;
                case IGKXMLNodeType::CDATA:
                case IGKXMLNodeType::DOCTYPE;
                $v=self::CreateElement($reader->NodeType);
                $v->
                Content=$reader->getValue();
                if(!$cnode){
                    $tab_doc->add($v);
                }
                else{
                    $cnode->add($v);
                }
                break;
                case IGKXMLNodeType::ENDELEMENT:
                $n=$reader->Name();
                $tnode=$cnode;
                if($cnode){
                    $t=$cnode->TagName;
                    if($context == "LoadExpression"){
                        if($n == $t){
                            $cnode=$reader->_LoadComplete($cnode, $n);
                        }
                        else{
                            $rsv=false;
                            $krsv=true;
                            $_shift_setting($n, $cnode, $v_tags, $krsv);
                            while($cnode && ($cnode->TagName != $n)){
                                $cnode=$reader->_LoadComplete($cnode, $n);
                                $rsv=true;
                            }
                            if(!$krsv && $cnode){
                                $cnode=$reader->_LoadComplete($cnode, $n);
                            }
                            else{
                                igk_die("[Bad Html structure] can't get parent, cnode is null, name : $n  , tagName : $t  ?  <br/>\n"."Line : ".__LINE__."<br />\n<code>".$text."</code><br />\n".IGK_LF."<br /><div >Context:".$context."</div>"." : ".get_class($cnode));
                            }
                        }
                        if($cnode == $tab_doc){
                            $cnode=null;
                            $reader->
                            m_nodes=array();
                        }
                    }
                    else{
                        if(($n == $t) || $cnode->isCloseTag($n) || $reader->IsResolved($cnode, $n)){
                            $cnode=$reader->_LoadComplete($cnode, $n);
                        }
                        else{
                            $rsv=false;
                            $krsv=true;
                            $pnode=$cnode;
                            $kclosing=true;
                            while($kclosing && $pnode){
                                if(igk_count($v_tags) > 0){
                                    $peek=$v_tags[0];
                                    if(($peek->clName == $n) && ($peek->item === $pnode)){
                                        array_shift($v_tags);
                                        $kclosing=0;
                                    }
                                    else{
                                        if($pnode->isEmptyTag()){}
                                        else if($pnode->TagName == $n){
                                            $kclosing=0;
                                        }
                                        else{
                                            igk_wln("failed ::::");
                                        }
                                    }
                                }
                                $pnode=$reader->_LoadComplete($pnode, $n);
                            }
                            $cnode=$pnode;
                        }
                        if($cnode == $tab_doc){
                            $cnode=null;
                            $reader->
                            m_nodes=array();
                        }
                    }
                }
                break;
                case IGKXMLNodeType::PROCESSOR:{
                    $v=$reader->getValue();
                    $v_cnode=new IGKHtmlProcessInstruction($v);
                    if($cnode == null){
                        $tab_doc[]=$cnode;
                    }
                    else{
                        $cnode->add($v_cnode);
                    }
                }
                break;
            }
        }
    }
