    ///<summary>create Element</summary>
    static public function CreateElement($nodetype, $value=IGK_STR_EMPTY){
        $v=null;
        switch($nodetype){
            case IGKXMLNodeType::CDATA:
            $v=(new IGKXmlNode("!CDATA"));
            $v->setContent($value);
            break;
            case IGKXMLNodeType::DOCTYPE:
            $v=new IGKHtmlDoctype($value);
            break;
        }
        return $v;
    }
