    ///<summary>Represente Read function</summary>
    public function Read(){
        if(!$this->CanRead()){
            $this->
            m_nodetype=IGKXMLNodeType::NONE;
            $this->
            m_v=null;
            $this->
            m_name=null;
            $this->
            m_isEmpty=true;
            $this->
            m_hasAttrib=false;
            $this->
            m_attribs=null;
            return false;
        }
        $v_enter=false;
        $this->
        m_isEmpty=false;
        $this->
        m_hasAttrib=false;
        $this->
        m_attribs=array();
        $v=IGK_STR_EMPTY;
        $v_c=strlen($this->m_text);
        while($this->CanRead()){
            $c=$this->m_text[$this->m_offset];
            switch($c){
                case "<":
                $v_enter=true;
                break;
                case "?":
                if($v_enter){
                    $this->m_offset++;
                    $v=IGK_STR_EMPTY;
                    while($this->CanRead()){
                        $v .= $this->m_text[$this->m_offset];
                        $this->m_offset++;
                        if(substr($v, -2, 2) == "?>"){
                            $v=substr($v, 0, strlen($v)-3);
                            $this->
                            m_name=null;
                            $this->
                            m_v=$v;
                            $this->
                            m_nodetype=IGKXMLNodeType::PROCESSOR;
                            return true;
                        }
                    }
                }
                else{
                    return $this->__readTextValue($v);
                }
                igk_debug_wln("failed");
                return false;
                break;
                case "!":
                if($v_enter){
                    if(substr($this->m_text, $this->m_offset + 1, 2) == "--"){
                        $this->m_offset += 3;
                        $v=IGK_STR_EMPTY;
                        while($this->CanRead()){
                            $v .= $this->m_text[$this->m_offset];
                            $this->m_offset++;
                            if(substr($v, -3, 3) == "-->"){
                                $v=substr($v, 0, strlen($v)-3);
                                $this->
                                m_name=null;
                                $this->
                                m_v=$v;
                                $this->
                                m_nodetype=IGKXMLNodeType::COMMENT;
                                return true;
                            }
                        }
                    }
                    else if(strtoupper(substr($this->m_text, $this->m_offset + 1, 7)) == "[CDATA["){
                        $this->m_offset += 8;
                        $v=IGK_STR_EMPTY;
                        while($this->CanRead()){
                            $v .= $this->m_text[$this->m_offset];
                            $this->m_offset++;
                            if(substr($v, -3, 3) == "]]>"){
                                $v=substr($v, 0, strlen($v)-3);
                                $this->
                                m_name=null;
                                $this->
                                m_v=$v;
                                $this->
                                m_nodetype=IGKXMLNodeType::CDATA;
                                return true;
                            }
                        }
                    }
                    else if(strtoupper(substr($this->m_text, $this->m_offset + 1, 7)) == "DOCTYPE"){
                        $this->m_offset += 8;
                        $v=IGK_STR_EMPTY;
                        while($this->CanRead()){
                            $v .= $this->m_text[$this->m_offset];
                            $this->m_offset++;
                            if(substr($v, -1, 1) == ">"){
                                $v=substr($v, 0, strlen($v)-1);
                                $this->
                                m_name=null;
                                $this->
                                m_v=$v;
                                $this->
                                m_nodetype=IGKXMLNodeType::DOCTYPE;
                                return true;
                            }
                        }
                    }
                    return false;
                }
                break;
                case "/":
                if($v_enter){
                    $this->m_offset += 1;
                    $this->
                    m_nodetype=IGKXMLNodeType::ENDELEMENT;
                    $this->
                    m_name=$this->ReadName();
                    $this->
                    m_v=null;
                    $v_enter=false;
                    while(($v_c > $this->m_offset) && ($this->m_text[$this->m_offset] !== '>')){
                        $this->m_offset++;
                    }
                    return true;
                }
                $v .= $c;
                break;default: 
                if(!$v_enter){
                    if($this->m_nodetype == IGKXMLNodeType::ELEMENT){
                        $match=array();
                        $tag=strtolower($this->m_name);
                        switch($tag){
                            case "script":
                            case "code":{
                                while($this->CanRead()){
                                    $v .= $this->m_text[$this->m_offset];
                                    $this->m_offset++;
                                    if(preg_match("/\<\/([\s]*)".$tag."([\s]*)\>$/i", $v, $match)){
                                        $this->m_offset -= strlen($match[0]);
                                        $v=substr($v, 0, strlen($v) - strlen($match[0]));
                                        break;
                                    }
                                }
                                $this->
                                m_name=$tag;
                                $this->
                                m_v=$v;
                                $this->
                                m_nodetype=IGKXMLNodeType::TEXT;
                                return true;
                            }
                            break;
                            case "style":{
                                while($this->CanRead()){
                                    $v .= $this->m_text[$this->m_offset];
                                    $this->m_offset++;
                                    if(preg_match("/(\<\/([\s]*)style([\s]*)>)$/i", $v, $match)){
                                        $this->m_offset -= strlen($match[0]);
                                        $v=substr($v, 0, strlen($v) - strlen($match[0]));
                                        break;
                                    }
                                }
                                $this->
                                m_name=null;
                                $this->
                                m_v=$v;
                                $this->
                                m_nodetype=IGKXMLNodeType::TEXT;
                                return true;
                            }
                            break;default: {
                                return $this->__readTextValue($v);
                            }
                            break;
                        }
                    }
                    else{
                        $v=IGK_STR_EMPTY;
                        if($this->m_nodetype == IGKXMLNodeType::ENDELEMENT)
                            $this->m_offset++;
                        while($this->CanRead()){
                            $v .= $this->m_text[$this->m_offset];
                            $this->m_offset++;
                            if(substr($v, -1, 1) == "<"){
                                $v=substr($v, 0, strlen($v)-1);
                                $this->m_offset--;
                                $this->
                                m_name=null;
                                $this->
                                m_v=$v;
                                $this->
                                m_nodetype=IGKXMLNodeType::TEXT;
                                return true;
                            }
                        }
                    }
                }
                else{
                    $this->
                    m_name=$this->ReadName();
                    $this->
                    m_v=null;
                    $this->
                    m_nodetype=IGKXMLNodeType::ELEMENT;
                    $this->
                    m_isEmpty=false;
                    $this->
                    m_hasAttrib=false;
                    $v_end=false;
                    $v=IGK_STR_EMPTY;
                    $v_readname=false;
                    $v_readvalue=false;
                    $v_attribname=null;
                    $v_ch=null;
                    $v_startattribvalue=false;
                    $v_attribmatch=IGK_STR_EMPTY;
                    $v_bracketstart=false;
                    $v_bracketch="";
                    $v_expressions=array();
                    while($this->CanRead()){
                        $v_ch=$this->m_text[$this->m_offset];
                        $this->m_offset++;
                        if(preg_match("/(\[)/", $v_ch)){
                            if(!$v_startattribvalue){
                                $exp=$v_ch. self::__readBracket($this->m_text, $this->m_offset, '[', ']');
                                $cout=igk_count($v_expressions);
                                $v_expressions[]=$exp;
                                $v .= "@igk:expression$cout=\"$cout\"";
                                continue;
                            }
                        }
                        $v .= $v_ch;
                        if(preg_match("('|\")", $v_ch)){
                            if($v_startattribvalue){
                                if($v_attribmatch == $v_ch)
                                    $v_startattribvalue=false;
                            }
                            else{
                                $v_startattribvalue=true;
                                $v_attribmatch=$v_ch;
                            }
                        }
                        if($v_startattribvalue)
                            continue;
                        if(substr($v, -2, 2) == "/>"){
                            $v=trim(substr($v, 0, strlen($v)-2));
                            $this->
                            m_isEmpty=true;
                            break;
                        }
                        else if(substr($v, -1, 1) == ">"){
                            $v=substr($v, 0, strlen($v)-1);
                            $this->
                            m_isEmpty=false;
                            break;
                        }
                        if($v_readname == false){
                            if(preg_match("/([\s])/", $v_ch)){
                                $v_attribname=$v_ch;
                                $v_readname=true;
                                $v_readvalue=false;
                            }
                        }
                        else{
                            if(preg_match("/[\s]/", $v_ch))
                                $v_attribname .= $v_ch;
                            else{
                                $v_readname=false;
                            }
                        }
                    }
                    if(!empty($v)){
                        $this->
                        m_hasAttrib=true;
                        $m=null;
                        $machv="(?P<value>";
                        $machv .= "([\"](([^\"]*(')?(\"\")?)+)[\"])";
                        $machv .= "|([\'](([^']*(\")?('')?)+)[\'])";
                        $machv .= "|(([^\s]*)+)";
                        $machv .= ")";
                        $tagRegexLoad="(?P<name>("."([\(])".IGK_TAGNAME_CHAR_REGEX."+([\)])"."|([\\[])".IGK_TAGNAME_CHAR_REGEX."+([\\]])".'|(@|\*(\*))?'.IGK_TAGNAME_CHAR_REGEX.'+'."))";
                        $rgx="/".$tagRegexLoad."[\s]*=[\s]*(".$machv.")/i";
                        $acount=preg_match_all($rgx, $v, $m);
                        $this->
                        m_attribs=array();
                        for($cc=0; $cc < $acount; $cc++){
                            $k=$m["name"][$cc];
                            if(preg_match("/^@igk:expression/", $k)){
                                $this->
                                m_attribs[$k]=$v_expressions[IGKHtmlUtils::GetAttributeValue($m["value"][$cc], $this->context)];
                            }
                            else{
                                if((strlen($k) > 2) && preg_match("/^\*\*/i", $k)){
                                    $this->m_attribs["[".substr($k, 2).
                                    "]"]=IGKHtmlUtils::GetAttributeValue($m["value"][$cc], $this->context);
                                }
                                else
                                    $this->
                                m_attribs[$k]=IGKHtmlUtils::GetAttributeValue($m["value"][$cc], $this->context);
                            }
                        }
                    }
                    return true;
                }
                break;
            }
            $this->m_offset += 1;
        }
        if(!$v_enter && !empty($v)){
            $this->
            m_name=null;
            $this->
            m_v=$v;
            $this->
            m_nodetype=IGKXMLNodeType::TEXT;
            return true;
        }
        return false;
    }
    ///read tag name
    public function ReadName(){
        $v=IGK_STR_EMPTY;
        while($this->CanRead() && preg_match("/".IGK_TAGNAME_CHAR_REGEX."/i", $this->m_text[$this->m_offset])){
            $v .= $this->m_text[$this->m_offset];
            $this->m_offset++;
        }
        return $v;
    }
    ///<summary>set root node creator</summary>
    static public function SetCreator($listener){
        self::
        $sm_ItemCreatorListener=$listener;
    }
    ///<summary>Represente Skip function</summary>
    public function Skip(){
        if($this->m_nodetype == IGKXMLNodeType::ELEMENT){
            if(!$this->m_isEmpty){
                $n=$this->Name();
                $depth=0;
                $end=false;
                while(!$end && $this->Read()){
                    switch($this->m_nodetype){
                        case IGKXMLNodeType::ELEMENT:
                        $depth++;
                        break;
                        case IGKXMLNodeType::ENDELEMENT:
                        if(($depth == 0) && (strtolower($this->Name()) == strtolower($n))){
                            $end=true;
                        }
                        else if($depth > 0)
                            $depth--;
                        break;
                    }
                }
                return $end;
            }
        }
        return false;
    }
}
