    ///<summary>register script do manager</summary>
    public function addScript($file, $canbeMerged=true, $tag='priv'){
        $local=false;
        $fname=true;
        $f=$file;
        if(!IGKValidator::IsUri($file)){
            $fname=false;
            $f=$file;
            if(file_exists($f)){
                $file=igk_html_uri(realpath($f));
                $local=true;
            }
            else{
                $local=false;
            }
        }
        $tasc=$this->getAssoc();
        if(isset($tasc[$file])){
            return $tasc[$file];
        }
        $g=!$fname && !$local;
        $tasc[$file]=$canbeMerged ? 1: 2;
        $this->_f->updateFlag(self::JSMAN_ASSOC_TABLE_FLAG, $tasc);
        return $tasc[$file];
