    public function Clear($tag=null){
        if($tag == null){
            $this->
            m_assocTable=array();
            $this->m_node->ClearChilds();
            return;
        }
        $t=array();
        foreach($this->m_assocTable as $k=>$v){
            if($v->tag == $tag){
                igk_html_rm($v);
                continue;
            }
            $t[$k]=$v;
