    ///<summary>Represente __sleep function</summary>
    public function __sleep(){
        if($this->m_openCount > 1){
            igk_die("open count must not be greather than 1");
        }
        return array();
    }
    ///<summary>Represente __wakeup function</summary>
    public function __wakeup(){
        igk_wln_e("wake up not allowed ", $this->m_openCount);
        $this->
        m_resource=null;
        $this->
        m_openCount=0;
    }
    ///<summary>Represente close function</summary>
    ///<param name="leaveOpen" default="false"></param>
    public function close($leaveOpen=false){
        if($this->getIsConnect()){
            if($leaveOpen && ($this->m_openCount == 1)){
                return;}$this->m_openCount--;
            if($this->m_openCount<=0){
                if(igk_db_is_resource($this->m_resource))
                    igk_mysql_db_close($this->m_resource);
                $this->
                m_isconnect=false;
                $this->
                m_resource=null;
                self::SetResId(null, __FUNCTION__);
                $this->
                m_openCount=0;
            }
        }
    }
    ///<summary>Represente closeAll function</summary>
    public function closeAll(){
        if(igk_db_is_resource($this->m_resource)){
            igk_mysql_db_close($this->m_resource);
            $this->
            m_isconnect=false;
            $this->
            m_resource=null;
            $this->
            m_openCount=0;
        }
        if($this->m_closeCallback){
            call_user_func_array($this->m_closeCallback, array());
        }
    }
    ///<summary>Represente connect function</summary>
    public function connect(){
        if($this->m_isconnect && $this->m_resource){
            if(@$this->m_resource->ping()){
                self::SetResId($this->m_resource, __FUNCTION__);
                $this->m_openCount++;
                igk_set_env("lastCounter", igk_ob_get_func('igk_trace'));
                return true;
            }
            $lcount=$this->m_openCount;
            $this->
            m_openCount=0;
            $this->
            m_isconnect=0;
            $this->
            m_openCount=0;
            igk_die("[igk] The connection was not closed properly ::: ping failed : ".$lcount." <br />".igk_ob_get_func('igk_wln', array(
                igk_get_env("lastCounter"),
                $this,
                $this->getLastQuery()
            )));
        }
        $r=igk_db_connect($this->m_dbserver, $this->m_dbuser, $this->m_dbpwd);
        if(igk_db_is_resource($r)){
            self::SetResId($r, __FUNCTION__);
            $t=igk_db_query("SELECT SUBSTRING_INDEX(CURRENT_USER(),'@',1)");
            if($t && (igk_db_num_rows($t) == 1)){
                $this->
                m_isconnect=true;
                $this->
                m_resource=$r;
                $this->
                m_openCount=1;
                return true;
            }
        }
        else{
            $_error=__CLASS__."::Error : SERVER RESOURCE # ";
            igk_notify_error($_error, "sys");
        }
        self::SetResId(null, __FUNCTION__);
        $this->
        m_isconnect=false;
        $this->
        m_resource=null;
        return false;
    }
    ///<summary>Represente connectTo function</summary>
    ///<param name="dbserver"></param>
    ///<param name="dbname"></param>
    ///<param name="dbuser"></param>
    ///<param name="dbpwd"></param>
    public function connectTo($dbserver, $dbname, $dbuser, $dbpwd){
        if($this->m_isconnect)
            $this->close();
        $r=@igk_db_connect($dbserver, $dbuser, $dbpwd);
        if(igk_db_is_resource($r)){
            $t=igk_db_query("SELECT SUBSTRING_INDEX(CURRENT_USER(),'@',1)");
            if($t && (igk_db_num_rows($t) == 1)){
                $this->
                m_isconnect=true;
                $this->
                m_resource=$r;
                $this->
                m_openCount=1;
                $tt=$this->selectdb($dbname);
                if(!$tt){
                    igk_debug_wln("DB Not selected : ". $dbname);
                }
                return $tt;
            }
        }
        else{
            $s=igk_mysql_db_error();
            igk_notifyctrl()->addError("MySQLError  : ".$s);
            igk_debug_wln("ERROR : ". $s);
        }
        $this->
        m_isconnect=false;
        $this->
        m_resource=null;
        return false;
    }
