        if($this->m_instances == null){
            $this->
            m_instances=array();
        }
        if(isset($this->m_instances[$class])){
            if(get_class(($cl=$this->m_instances[$class])) != $class){
                die(__("Class instance changed"));
            }
            return $cl;
        }
        if($callback != null){
