///<summary>subdomain manager</summary>
final class IGKSubDomainManager extends IGKObject{
    static private $sm_instance;
    static private $sm_isSubDomain;
    static private $sm_subDomainName;
    ///<summary>Represente __construct function</summary>
    private function __construct(){}
    ///<summary>Represente AcceptDomain function</summary>
    ///<param name="domain"></param>
    ///<param name="servername"></param>
    static public function AcceptDomain($domain, $servername){
        if(IGKValidator::IsIpAddress($domain) && IGKValidator::IsIpAddress($servername)){
            return false;
        }
        $x1=igk_io_path_ext($domain);
        $x2=igk_io_path_ext($servername);
        if($x1 == $x2){
            $x1=igk_io_basenamewithoutext($domain);
            $x2=igk_io_basenamewithoutext($servername);
            if(preg_match("/\.".$x1."$/i", $x2)){
                return true;
            }
        }
        return false;
    }
    ///<summary>get the domain controller or return false</summary>
    public function checkDomain($uri=null, & $row=null){
        if(igk_is_atomic()){
            return false;
        }
        $subdomain=igk_io_subdomain_uri_name($uri);
        $t=$this->getRegList();
        if(!empty($subdomain)){
            $s=$subdomain;
            if(isset($t[$s])){
                $c=$t[$s];
                $row=$c->row;
                return $c->ctrl;
            }
            $row=null;
            $c=igk_getctrl(IGK_SUBDOMAINNAME_CTRL)->getDomainCtrl($s, $row);
            if(($c != null)){
                if(!$this->reg_domain($s, $c, $row))
                    igk_die("/!\\ Fatal can't register function");
                return $c;
            }
        }
        return false;
    }
    ///<summary>Represente Clear function</summary>
    public function Clear(){
        igk_app()->setParam(IGK_SESS_DOM_LIST, null);
    }
    ///<summary>Represente domainChanged function</summary>
    public function domainChanged(){
        $this->Clear();
    }
    ///<summary>Represente domainList function</summary>
    public function domainList(){
        $t=$this->getRegList();
        return array_keys($t);
    }
    ///<summary>Represente GetBaseDomain function</summary>
    static public function GetBaseDomain(){
        $f=igk_io_basedir()."/".IGK_DATA_FOLDER."/domain.conf";
        $d="";
        if(file_exists($f)){
            $d=IGKIO::ReadAllText($f);
        }
        if(empty($d))
            $d=IGK_DOMAIN;
        return $d;
    }
    ///<summary>Represente getInstance function</summary>
    static public function getInstance(){
        if(self::$sm_instance == null){
            $k=new IGKSubDomainManager();
            self::
            $sm_instance=$k;
        }
        return self::$sm_instance;
    }
    ///<summary>Represente getRegList function</summary>
    public function getRegList(){
        return igk_app()->Session->getParam(IGK_SESS_DOM_LIST, function(){
            return array();
        });
    }
    ///<summary>Represente GetSubDomain function</summary>
    static public function GetSubDomain(){
        $srv=igk_io_server_name();
        if(preg_match("/^(www\.)/i", $srv)){
            $srv=substr($srv, 4);
        }
        if(!empty($srv)){
            $d=self::GetBaseDomain();
            if(preg_match("/(\.".$d."$)/i", $srv)){
                $srv=substr($srv, 0, strlen($srv) - strlen($d)-1);
            }
            else
                $srv="";
        }
        return $srv;
    }
    ///<summary>Represente GetSubDomainName function</summary>
    static public function GetSubDomainName(){
        return self::$sm_subDomainName;
    }
    ///<summary>init domain server</summary>
    static public function Init(){
        self::
        $sm_isSubDomain=false;
        self::
        $sm_subDomainName=false;
        $srv=igk_sys_srv_domain_name();
        $domain=self::GetBaseDomain();
        $rdomain=null;
        if(($srv !== "localhost") && !IGKValidator::IsIPAddress($srv) && ($srv !== $domain) && (preg_match("/(www)?\.".$domain."$/i", $srv) || self::AcceptDomain($domain, $srv))){
            $rdomain=self::Resolv($domain);
            ini_set("session.cookie_domain", ".".$rdomain);
            self::
            $sm_isSubDomain=true;
            self::
            $sm_subDomainName=self::GetSubDomain();
        }
        else{
            $rdomain=$srv;
            $_path="/";
            if(IGKValidator::IsIPAddress($srv)){}
            else{
                if(igk_server_request_onlocal_server())
                    $srv="localhost";
                else
                    $srv=".".$srv;
            }
            if(!empty($bdir=igk_io_rootbasedir())){
                $_path=$bdir;
            }
            ini_set("session.cookie_domain", ($srv == "localhost") ? null: $srv);
            ini_set("session.cookie_path", $_path);
        }
    }
    ///<summary>Represente IsControl function</summary>
    ///<param name="n"></param>
    ///<param name="ctrl"></param>
    static public function IsControl($n, $ctrl){
        $t=self::getInstance()->getRegList();
        return isset($t[$n]) && ($t[$n]->ctrl === $ctrl);
    }
    ///<summary>Represente IsSubDomain function</summary>
    static public function IsSubDomain(){
        return self::$sm_isSubDomain;
    }
    ///<summary>Represente reg_domain function</summary>
    ///<param name="n"></param>
    ///<param name="ctrl"></param>
    ///<param name="row" default="null"></param>
    public function reg_domain($n, $ctrl, $row=null){
        if(empty($n) || !igk_reflection_class_implement($ctrl, "IIGKUriActionRegistrableController")){
            return false;
        }
        $t=$this->getRegList();
        if(!isset($t[$n])){
            $t[$n]=(object)array("ctrl"=>$ctrl, "row"=>$row);
            $this->updateRegList($t);
            return true;
        }
        return false;
    }
    ///<summary>resole domain to match server name</summary>
    static public function Resolv($domain){
        $servername=igk_io_server_name();
        $ex1=igk_io_path_ext($domain);
        $ex2=igk_io_path_ext($servername);
        if($ex1 == $ex2)
            return $domain;
        $x1=igk_io_basenamewithoutext($domain);
        $x2=igk_io_basenamewithoutext($servername);
        if(preg_match("/\.".$x1."$/i", $x2))
            return $x1.".".$ex2;
        return $domain;
    }
    ///<summary>Represente StoreBaseDomain function</summary>
    ///<param name="ctrl"></param>
    ///<param name="bDomain"></param>
    static public function StoreBaseDomain($ctrl, $bDomain){
        igk_io_save_file_as_utf8_wbom(igk_io_applicationdatadir()."/domain.conf", $bDomain, true);
    }
    ///<summary>Represente updateRegList function</summary>
    ///<param name="t"></param>
    private function updateRegList($t){
        igk_app()->Session->setParam(IGK_SESS_DOM_LIST, $t);
    }
}
