    static public function IsStringNullOrEmpty($v, $cibling=null, $msg="error..."){
        $v=(($v == null) || (is_string($v) && (strlen($v) == 0)));
        if($v && $cibling){
            $cibling->addError($msg);
        }
        return $v;
    }
    ///<summary>check if full uri</summary>
    static public function IsUri($v){
        if(empty($v))
            return false;
        $r=preg_match('/^(((http(s){0,1}):)?\/\/([\w\.0-9]+)|(\?))/i', $v);
        return $r;
    }
