        return false;
    }
    ///<summary>Represente Validate function</summary>
    ///<param name="o"></param>
    ///<param name="fields"></param>
    ///<param name="error" ref="true"></param>
    static public function Validate($o, $fields, & $error){
        $g=self::getInstance()->sm_enode;
        $g->ClearChilds();
        $e=false;
        if(is_array($fields)){
            foreach($fields as $k=>$v){
                $s=$o->$k;
                $cond=call_user_func_array(array(__CLASS__, $v["f"]), array($s));
                self::Assert($cond, $e, $g, $v["e"]);
            }
        }
        return !$e;
    }
}
