    ///<summary>get new language expression</summary>
    static public function ngets($key){
        if($key == null)
            return null;
        if(!is_string($key)){
            return $key;
        }
        if(self::$sm_keyVAR == null)
            self::
        $sm_keyVAR=array();
        $T=strtolower($key);
        $default=self::gets($T);
        $args=array();
        if(func_num_args() > 1){
            $t=func_get_args();
            for($i=1; $i < count($t); $i++){
                if(($i == 1) && is_array($t[$i])){
                    $args=array_merge($args, $t[$i]);
                }
                else
                    $args[]=$t[$i];
            }
            return new IGKLangKey($key, $default, $args);
        }
        else{
            if(isset(self::$sm_keyVAR[$T])){
                return self::$sm_keyVAR[$T];
            }
            else{
                self::
                $sm_keyVAR[$T]=new IGKLangKey($key, $default, $args);
                return self::$sm_keyVAR[$T];
            }
        }
    }
