        $_instance=self::getInstance();
        if($_instance->m_langctrl == null)
            $_instance->
        m_langctrl=array();
        if(isset($_instance->m_langctrl[$ctrl->Name]))
            unset($_instance->m_langctrl[$ctrl->Name]);
    }
}
