if (\$cl != 'IGKHtmlDocTheme')
{
	igk_die("this file can be only included in IGKHtmlDocTheme context");
}

EOF;
        $out .= $this->getDeclaration();
        $out .= IGK_START_COMMENT."media properties ".IGK_END_COMMENT.IGK_LF;
        foreach($this->m_medias as $k=>$v){
