    ///<summary>Represente GetColumnList function</summary>
    ///<param name="options"></param>
    ///<param name="tbname"></param>
    static public function GetColumnList($options, $tbname){
        $o="*";
        if($options && ($c=igk_getv($options, 'Columns'))){
            $s="";
            foreach($c as $k=>$v){
                if(!empty($s))
                    $s .= ", ";
                $s .= $tbname.".".$k;
                if(is_string($v) && ($v != $k)){
                    $s .= " as ".$v;
                }
            }
            if(!empty($s))
                $o=$s;
        }
        return $o;
    }
    ///<summary>Represente GetCondString function</summary>
    ///<param name="tab"></param>
    ///<param name="operator" default="'AND'"></param>
    static public function GetCondString($tab, $operator='AND'){
        $query="";
        $t=0;
        $fc="getValue";
        $to="obj:type";
        $op=igk_db_escape_string($operator);
        $c_exp="IS NULL";
        if(is_numeric($tab)){
            return "`clId`='{$tab}'";
        }
        if(is_object($tab)){
            if(get_class($v) == IGKDbExpression::class){
                return $v->getValue();
            }
        }
        foreach($tab as $k=>$v){
            if(is_object($v)){
                if(get_class($v) == IGKDbExpression::class){
                    $query .= $v->getValue();
                    continue;
                }
            }
            $c="=";
            if($t == 1)
                $query .= " $op ";
            if(is_object($v)){
                if(isset($v->$to)){
                    switch($v->$to){
                        case "igk_db_field":
                        if(is_array($v->clName)){
                            $hh=array();
                            foreach($v->clName as $ks){
                                $hh[]="`".igk_db_escape_string(str_replace(".", "`.`", $ks))."`";
                            }
                            $query .= igk_str_join_tab($hh, $v->clOperator, false);
                        }
                        break;
                    }
                }
                else{
                    $query .= "`".igk_obj_call($v, $fc)."`";
                }
            }
            else{
                if(preg_match("/^(!|#)/", $k)){
                    $ch=substr($k, 0, 1);
                    $k=substr($k, 1);
                    if($ch == '!'){
                        $c="!=";
                        $c_exp="IS NOT NULL";
                    }
                }
                $query .= "`".igk_db_escape_string($k);
                if($v !== null)
                    $query .= "`{$c}'".igk_db_escape_string($v)."'";
                else
                    $query .= "` ".$c_exp;
            }
            $t=1;
        }
        return $query;
    }
    ///<summary>Represente GetDeleteQuery function</summary>
