    ///<summary>Represente Sort function</summary>
    ///<param name="tab" ref="true"></param>
    ///<param name="key" default="null"></param>
    public function Sort(& $tab, $key=null){
        if(is_array($tab)){
            usort($tab, array($this, "SortValue"));
        }
        else{
            if(method_exists(get_class($tab), "SortValueBy")){
                $tab->SortValueBy($this->key);
            }
        }
        if($key){
            $b=array();
            foreach($tab as $k=>$v){
                $b[igk_getv($v, $key)
                ]=$v;
            }
            $tab=$b;
        }
    }
    ///<summary>Represente SortByDisplay function</summary>
    ///<param name="tab"></param>
    ///<param name="key"></param>
    ///<param name="asc" default="true"></param>
    static public function SortByDisplay($tab, $key, $asc=true){
        return self::__SortValue($tab, $key, $asc, "SortKeyValue");
    }
    ///<summary>Represente SortByValue function</summary>
    ///<param name="tab"></param>
    ///<param name="key"></param>
    ///<param name="asc" default="true"></param>
    static public function SortByValue($tab, $key, $asc=true){
        return self::__SortValue($tab, $key, $asc, "SortValue");
    }
    ///<summary>Represente SortKeyValue function</summary>
    ///<param name="a"></param>
    ///<param name="b"></param>
    public function SortKeyValue($a, $b){
        $k=$this->key;
        $s1=strtolower(__($a->$k));
        $s2=strtolower(__($b->$k));
        $i=strcmp($s1, $s2);
        return $i;
    }
    ///<summary>Represente SortValue function</summary>
    ///<param name="a"></param>
    ///<param name="b"></param>
    public function SortValue($a, $b){
        $tk=$this->key;
        if(is_string($tk))
            $tk=array($tk=>$this->asc);
        $i=0;
        $o=0;
        $op=0;
        foreach($tk as $k=>$asc){
            $s1=
            $s2=null;
            if(is_object($a) && is_object($b)){
                $s1=strtolower(igk_getv($a, $k, ''));
                $s2=strtolower(igk_getv($b, $k, ''));
            }
            else{
                $s1=strtolower(igk_getv($a, $k, ''));
                $s2=strtolower(igk_getv($b, $k, ''));
            }
            if(is_integer($s1) && is_integer($s2)){
                $i=strnatcmp($s1, $s2);
            }
            else
                $i=strcmp($s1, $s2);
            if(($i != 0) && (!$asc)){
                $i *= -1;
            }
            if($i == 0){
                break;
            }
            $o=$i;
            $op=1;
        }
        return $o;
    }
}
///<summary>represent a string utility class</summary>
class IGKString{
    ///<summary>Represente Contains function</summary>
    ///<param name="text"></param>
    ///<param name="pattern"></param>
    static public function Contains($text, $pattern){
        if(!empty($pattern))
            return (strstr($text, $pattern) != null);
        return true;
    }
    ///<summary>Represente EndWith function</summary>
    ///<param name="chaine"></param>
    ///<param name="pattern"></param>
    static public function EndWith($chaine, $pattern){
        return igk_str_endwith($chaine, $pattern);
    }
    ///<summary>Represente Format function</summary>
    ///<param name="s"></param>
    static public function Format($s){
        $c=preg_match_all("/\{(?P<value>[0-9]+)\}/i", $s, $match);
        if($c > 0){
            $args=array_slice(func_get_args(), 1);
            for($i=0; $i < $c; $i++){
                $index=$match["value"][$i];
                if(is_numeric($index)){
                    if(isset($args[$index])){
                        $s=str_replace($match[0][$i], IGKHtmlUtils::GetValue($args[$index]), $s);
                    }
                }
            }
        }
        return $s;
    }
    ///<summary>Represente IndexOf function</summary>
    ///<param name="chaine"></param>
    ///<param name="research"></param>
    ///<param name="offset"></param>
    //@chaine : string where to operate
    static public function IndexOf($chaine, $research, $offset=0){
        if(empty($chaine) || empty($research))
            return -1;
        $i=strpos($chaine, $research, $offset);
        if($i === false)
            return -1;
        return $i;
    }
    ///<summary>Represente Join function</summary>
    ///<param name="tab"></param>
    ///<param name="separator" default=","></param>
    ///<param name="key" default="true"></param>
    static public function Join($tab, $separator=",", $key=true){
        $s=IGK_STR_EMPTY;
        $t=0;
        if($tab){
            foreach($tab as $k=>$v){
                if($t == 1)
                    $s .= $separator;
                if($key)
                    $s .= $k;
                else
                    $s .= "".$v;
                $t=1;
            }
        }
        return $s;
    }
    ///<summary>Represente StartWith function</summary>
    ///<param name="chaine"></param>
    ///<param name="pattern"></param>
    static public function StartWith($chaine, $pattern){
        return (self::IndexOf($chaine, $pattern) == 0);
    }
    ///<summary>Represente Sub function</summary>
    ///<param name="chaine"></param>
    ///<param name="start"></param>
    ///<param name="length" default="null"></param>
    //@personal sub
    static public function Sub($chaine, $start, $length=null){
        if($length){
            return substr($chaine, $start, $length);
        }
        else
            return substr($chaine, $start);
    }
    ///<summary>Represente Trim function</summary>
    ///<param name="chaine"></param>
    static public function Trim($chaine){
        return trim($chaine);
    }
}
