///<summary>represent system cache</summary>
abstract class IGKSysCache{
    static $LibFiles;
    ///<summary>Represente CacheLibFiles function</summary>
    ///<param name="force" default="false"></param>
    static public function CacheLibFiles($force=false){
        $f=self::LibCacheFile();
        if(empty($f))
            return;
        $t=3600;
        $expire=time() - $t;
        $el=IGK_LF;
        if(!$force && file_exists($f) && (filemtime($f) > $expire)){
            return;}
        $data=IGK_STR_EMPTY;
        $dir=IGK_LIB_DIR;
        $mdir=igk_html_uri(IGK_LIB_DIR);
        foreach(self::$LibFiles as $k=>$v){
            $data .= 'require_once(\''.$v.'\');'.$el;
        }
        $init='';
        $restore='';
        $phar=igk_phar_available() && strstr(IGK_LIB_DIR, Phar::running());
        if(!$phar){
            $init=<<<EOF
\$bck = getcwd();
chdir(IGK_LIB_DIR);
\$d = '{$dir}';
chdir(\$d);
EOF;
            $restore=<<<EOF
chdir(\$bck);
unset(\$bck);
EOF;
        }
        $date=igk_date_now();
        $out=<<<EOF
<?php
// Balafon lib cache - auto generate
// author: C.A.D BONDJE DOUE
// date : {$date}
${init}
{$data}
${restore}
EOF;
        igk_io_save_file_as_utf8($f, $out);
        self::Init_CachedHook();
        if(function_exists("opcache_invalidate"))
            opcache_invalidate($f, true);
    }
    ///<summary>Represente Init_CachedHook function</summary>
    ///<param name="e" default="null"></param>
    static public function Init_CachedHook($e=null){
        if(!file_exists($f=igk_io_cachedir()."/.htaccess")){
            igk_io_w2file($f, "deny from all");
        }
    }
    ///<summary>Represente LibCacheFile function</summary>
    static private function LibCacheFile(){
        return igk_io_syspath(IGK_FILE_LIB_CACHE);
    }
    ///<summary>Represente LoadCacheLibFiles function</summary>
    static public function LoadCacheLibFiles(){
        $f=self::LibCacheFile();
        $v=false;
        if(!defined("IGK_NO_CACHE_LIB") && file_exists($f)){
            igk_invalidate_opcache($f);
            try {
                include_once($f);
                $v=true;
                igk_get_env_lib_loaded(true);
            }
            catch(Exception $ex){
                igk_wln("[ ".__FUNCTION__." ]- can't load files...[".$f."]");
                igk_wln_e("LoadLibError", $ex);
            }
        }
        return $v;
    }
}
///<summary>Represente class: IGKUserAgent</summary>
class IGKUserAgent{
    const REGEX_ANDROID="android";
    const REGEX_ANDROID_BUILDNUMBER="android\s+(?P<version>[0-9\.]+);\s*(?P<model>[\w0-9\.]+)\s+build\/(?P<buildnumber>[a-z0-9\.]+)";
    const REGEX_ANDROID_MODELNUMBER="android\s+(?P<version>[0-9\.]+);\s*(?P<model>[\w0-9\.]+)\s*";
    const REGEX_ANDROID_VERSION="android\s+(?P<version>[0-9\.]+);";
    ///<summary>Represente Agent function</summary>
    static public function Agent(){
        return igk_server()->HTTP_USER_AGENT;
    }
    ///<summary>Represente CheckSafariVersion function</summary>
    ///<param name="callback"></param>
    static public function CheckSafariVersion($callback){
        $v=self::GetSafariVersion();
        if($v){
            $t=explode(".", $v);
            return $callback($t);
        }
        return -1;
    }
    ///<summary>Represente GetAndroidBuildNumber function</summary>
    static public function GetAndroidBuildNumber(){
        if(self::IsAndroid()){
            $regex="/".self::REGEX_ANDROID_BUILDNUMBER."/i";
            $tab=array();
            preg_match_all($regex, self::Agent(), $tab);
            return $tab["buildnumber"][0];
        }
        return null;
    }
    ///<summary>Represente GetAndroidModel function</summary>
    static public function GetAndroidModel(){
        if(self::IsAndroid()){
            $regex="/".self::REGEX_ANDROID_MODELNUMBER."/i";
            $tab=array();
            preg_match_all($regex, self::Agent(), $tab);
            return $tab["model"][0];
        }
        return null;
    }
    ///<summary>Represente GetAndroidVersion function</summary>
    static public function GetAndroidVersion(){
        if(self::IsAndroid()){
            $regex="/".self::REGEX_ANDROID_VERSION."/i";
            $tab=array();
            preg_match_all($regex, self::Agent(), $tab);
            return $tab["version"][0];
        }
        return null;
    }
    ///<summary>Represente GetChromeVersion function</summary>
    static public function GetChromeVersion(){
        if(self::IsChrome()){
            $v_r="/Chrome\/\s*(?P<version>[0-9\.]+)\s/i";
            $tab=array();
            preg_match_all($v_r, self::Agent(), $tab);
            return $tab["version"][0];
        }
        return null;
    }
    ///<summary>Represente GetDefaultLang function</summary>
    static public function GetDefaultLang(){
        static $deflang=null;
        if($deflang == null){
            $regex="/^(?P<name>\w+)(,*)/i";
            $tab=array();
            $r=igk_server()->HTTP_ACCEPT_LANGUAGE;
            if($r){
                preg_match_all($regex, $r, $tab);
                $deflang=$tab["name"][0];
            }
            $deflang=IGK_DEFAULT_LANG;
        }
        return $deflang;
    }
    ///<summary>Represente GetSafariVersion function</summary>
    static public function GetSafariVersion(){
        if(self::IsSafari()){
            $v_r="/Safari\/\s*(?P<version>[0-9\.]+)(\s*)/i";
            $tab=array();
            preg_match_all($v_r, self::Agent(), $tab);
            return $tab["version"][0];
        }
        return null;
    }
    ///<summary>Represente IsAndroid function</summary>
    static public function IsAndroid(){
        $regex="/".self::REGEX_ANDROID."/i";
        return preg_match($regex, self::Agent());
    }
    ///<summary>Represente IsChrome function</summary>
    static public function IsChrome(){
        if(strstr(self::Agent(), "Chrome"))
            return true;
        return false;
    }
    ///<summary>Represente IsIE function</summary>
    static public function IsIE(){
        return preg_match("#(MSIE|Trident/|Edge/)#i", self::Agent());
    }
    ///<summary>Represente IsIOS function</summary>
    static public function IsIOS(){
        return false;
    }
    ///<summary>Represente IsMobileDevice function</summary>
    static public function IsMobileDevice(){
        return self::IsAndroid();
    }
    ///<summary>Is Firefox modzilla</summary>
    static public function IsMod(){
        if(strstr(self::Agent(), "Firefox"))
            return true;
        return false;
    }
    ///<summary>Old safari version</summary>
    static public function IsOldSafariAgent(){
        $v=IGKUserAgent::CheckSafariVersion(function($t){
            return $t[0] < 600;
        });
        if($v === -1)
            return false;
        return $v;
    }
    ///<summary>Represente IsSafari function</summary>
    static public function IsSafari(){
        return !self::IsChrome() && !self::IsMod() && strstr(self::Agent(), "Safari");
    }
    ///<summary>Represente IsXBox function</summary>
    static public function IsXBox(){
        $regex="/xbox/i";
        return preg_match($regex, self::Agent());
    }
    ///<summary>Represente IsXBoxOne function</summary>
    static public function IsXBoxOne(){
        $regex="/xbox one/i";
        return preg_match($regex, self::Agent());
    }
}
